mapper.update:
update orders

    
        status=#{status},
    
    
        rejection_reason=#{rejectionReason},
    
    
        cancel_reason=#{cancelReason},
    
    
        cancel_time=#{cancelTime}
    

where id = #{id}

update orders

    
        status=#{status},
    
    
        cancel_reason=#{cancelReason},
    
    
        cancel_time=#{cancelTime}
    

where id = #{id}

mapper.select:
select * from orders

    
        and phone = #{phone}
    
    
        and number = #{number}
    
    
        and order_time BETWEEN #{beginTime} and #{endTime}
    
    
        and status = #{status}

select sum(amount) from orders

    
        and status = #{status}
    
    
        and order_time >= #{begin}
    
    
        and order_time <= #{end}

select count(id) from orders

    
        and status = #{status}
    
    
        and order_time >= #{begin}
    
    
        and order_time <= #{end}
