mapper.update:
update shopping_cart

    
        number = #{number}
    


    
        and dish_id = #{dishId}
    
    
        and setmeal_id = #{setmealId}
    
    
        and user_id = #{userId}

mapper.delete:
delete from shopping_cart

    
        and user_id = #{userId}
    
    
        and dish_id = #{dishId}
    
    
        and setmeal_id = #{setmealId}

mapper.select:
select number from shopping_cart

    
        and dish_id = #{dishId}
    
    
        and setmeal_id = #{setmealId}
    
    
        and user_id = #{userId}
